(function(){tinymce.PluginManager.requireLangPack('filelink');tinymce.create('tinymce.plugins.FilelinkPlugin',{init:function(ed,url){ed.addCommand('mceFilelink',function(){ed.windowManager.open({file:url+'/filelink.php',width:425+parseInt(ed.getLang('filelink.delta_width',0)),height:160+parseInt(ed.getLang('filelink.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('filelink',{title:'Insert File',cmd:'mceFilelink',image:url+'/img/filelink.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('filelink',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Filelink plugin',author:'Iisakki Pirila',authorurl:'http://www.piuha.fi',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/filelink',version:"1.0"}}});tinymce.PluginManager.add('filelink',tinymce.plugins.FilelinkPlugin)})();